import React, { useEffect, useState } from 'react';
import API from '../api';
import PaymentModal from './PaymentModal';
import '../App.css';

export default function Wallet({ licensePlate }) {
  const [wallet, setWallet] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const fetchWallet = async () => {
    const res = await API.get(`/wallet/${licensePlate}`);
    setWallet(res.data.wallet);
    setTransactions(res.data.transactions);
  };

  useEffect(() => {
    fetchWallet();
  }, []);

  const handleAddMoney = async (amount, method) => {
    await API.post('/wallet/add', { licensePlate, amount, method });
    fetchWallet();
  };

  return (
    <div>
      <h4>Wallet: ₹{wallet}</h4>
      <button onClick={() => setShowModal(true)}>Add Money</button>
      <PaymentModal show={showModal} onClose={() => setShowModal(false)} onAdd={handleAddMoney} />
      <h5>Recent Transactions</h5>
      <ul>
        {transactions.map(txn => (
          <li key={txn._id}>{txn.type === 'add' ? '+' : '-'}₹{txn.amount} ({txn.method})</li>
        ))}
      </ul>
    </div>
  );
}
