import React, { useEffect, useState } from 'react';
import API from '../api';
import Wallet from './Wallet';

export default function Dashboard({ user, setUser }) {
  const [slots, setSlots] = useState([]);
  const [msg, setMsg] = useState('');
  const [reserved, setReserved] = useState(false);

  useEffect(() => {
    fetchSlots();
  }, []);

  const fetchSlots = async () => {
    const res = await API.get('/slots');
    setSlots(res.data);
  };

  const handleReserve = async () => {
    try {
      const res = await API.post('/slots/reserve', { licensePlate: user.licensePlate });
      setMsg(`Reserved slot #${res.data.slotNumber}`);
      setReserved(true);
      fetchSlots();
    } catch (e) {
      setMsg(e.response.data.error);
    }
  };

  const handleExit = async () => {
    try {
      const res = await API.post('/slots/release', { licensePlate: user.licensePlate });
      setMsg(`Exited. Charge: ₹${res.data.charge}`);
      setReserved(false);
      fetchSlots();
    } catch (e) {
      setMsg(e.response.data.error);
    }
  };

  const available = slots.filter(s => !s.occupied).length;

  return (
    <div>
      <h3>Welcome, {user.name}</h3>
      <div>Parking Location: RVCE Ground Parking</div>
      <div>Available slots: {available}/8</div>
      <button onClick={handleReserve} disabled={reserved || available === 0}>Reserve Parking</button>
      <button onClick={handleExit} disabled={!reserved}>Exit</button>
      <div>{msg}</div>
      <Wallet licensePlate={user.licensePlate} />
      <button onClick={() => setUser(null)}>Logout</button>
    </div>
  );
}
